import React from 'react';
import { View, Text, StyleSheet } from 'react-native';
import { Card } from 'react-native-elements';

const DashboardCard = ({ totalProperties, totalTenants, totalPayments }) => {
    return (
        <Card containerStyle={styles.cardContainer}>
            <View style={styles.statContainer}>
                <Text style={styles.statLabel}>Total Properties</Text>
                <Text style={styles.statValue}>{totalProperties}</Text>
            </View>

            <View style={styles.statContainer}>
                <Text style={styles.statLabel}>Total Tenants</Text>
                <Text style={styles.statValue}>{totalTenants}</Text>
            </View>

            <View style={styles.statContainer}>
                <Text style={styles.statLabel}>Upcoming Payments</Text>
                <Text style={styles.statValue}>{totalPayments}</Text>
            </View>
        </Card>
    );
}

const styles = StyleSheet.create({
    cardContainer: {
        padding: 20,
        borderRadius: 8,
    },
    statContainer: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        marginBottom: 15,
    },
    statLabel: {
        fontSize: 16,
        color: '#333',
    },
    statValue: {
        fontSize: 18,
        fontWeight: 'bold',
        color: '#007BFF', // You can change this color to match your theme.
    }
});

export default DashboardCard;
