import React from 'react';
import { View, StyleSheet } from 'react-native';
import { Appbar, Card, Title, Paragraph, Button, FAB } from 'react-native-paper';
import DashboardCard from '../../../components/composite/Dashboard/landlordDashboardCard';

const LandlordDashboard = () => {
  return (
    <View style={styles.container}>

      <Appbar.Header>
        <Appbar.Content title="Dashboard" subtitle="Landlord Overview" />
        {/* You can add more Appbar actions or a profile picture here */}
      </Appbar.Header>

      <Card style={styles.card}>
        <Card.Content>
          <Title>Total Tenants</Title>
          <Paragraph>12</Paragraph>
        </Card.Content>
      </Card>

      <Card style={styles.card}>
        <Card.Content>
          <Title>Total Properties</Title>
          <Paragraph>5</Paragraph>
        </Card.Content>
      </Card>

      <Card style={styles.card}>
        <Card.Content>
          <Title>Next Payment</Title>
          <Paragraph>1st October 2023</Paragraph>
        </Card.Content>
      </Card>

      <Button 
        mode="contained" 
        style={styles.button}
        onPress={() => {/* Navigate to Manage Properties */}}
      >
        Manage Properties
      </Button>

      <Button 
        mode="contained" 
        style={styles.button}
        onPress={() => {/* Navigate to Manage Tenants */}}
      >
        Manage Tenants
      </Button>

      <Button 
        mode="contained" 
        style={styles.button}
        onPress={() => {/* Navigate to Payments */}}
      >
        Payments
      </Button>

      <FAB
        style={styles.fab}
        icon="plus"
        onPress={() => {/* Add New Property or Tenant */}}
      />

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  card: {
    margin: 10,
  },
  button: {
    margin: 10,
  },
  fab: {
    position: 'absolute',
    margin: 16,
    right: 0,
    bottom: 0,
  },
});

export default LandlordDashboard;
