import React from 'react';
import { Card, CardContent, Typography, Box } from '@mui/material';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';

const incomeData = [
    { name: 'Jan', income: 4000 },
    { name: 'Feb', income: 4200 },
    { name: 'Mar', income: 4500 },
    // ... other months
];


const IncomeCard = ({ monthlyIncome, incomeData }) => {
    
    return (
        <Card sx={{ width: 1, mt: 10, mb: 2, boxShadow: 3 }}>
            <CardContent>
                <Typography variant="h6" gutterBottom>
                    Monthly Income
                </Typography>
                <Typography variant="h4">
                    ${monthlyIncome}
                </Typography>
                
                {/* LineChart from recharts */}
                <Box sx={{ height: 150, mt: 2 }}>
                    <LineChart
                        width={300}
                        height={150}
                        data={incomeData}
                        margin={{
                            top: 5, right: 30, left: 20, bottom: 5,
                        }}
                    >
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="name" />
                        <YAxis />
                        <Tooltip />
                        <Legend />
                        <Line type="monotone" dataKey="income" stroke="#007BFF" activeDot={{ r: 8 }} />
                    </LineChart>
                </Box>
            </CardContent>
        </Card>
    );
}

export default IncomeCard;
