import React from 'react';
import { StyleSheet, ScrollView, View, Dimensions } from 'react-native';
import { Card, Title, Paragraph, Text, List } from 'react-native-paper';
import { NavigationContainer } from '@react-navigation/native';
import { LinearGradient } from 'expo-linear-gradient';
import { LineChart } from 'react-native-chart-kit';

const LandlordDashboard = ({ navigation }) => {
     const chartData = {
    labels: ['January', 'February', 'March', 'April', 'May', 'June'],
    datasets: [{
      data: [1500, 1600, 1550, 1700, 1650, 1750], // Sample data
      strokeWidth: 2,
    }],
  };

  return (
      <ScrollView style={styles.container}> 
          <LinearGradient colors={['#43ecf5','white']} style={styles.gradientContainer}>
            <View style={styles.content}>
               <Card style={styles.incomeCard}>
            <Card.Content>
              <Title>Expected Income</Title>
              <LineChart
                data={chartData}
                width={Dimensions.get('window').width - 40} // subtracting padding
                height={220}
                chartConfig={{
                  backgroundGradientFrom: '#fff',
                  backgroundGradientTo: '#fff',
                  decimalPlaces: 2,
                  color: (opacity = 1) => `rgba(0, 0, 0, ${opacity})`,
                  labelColor: (opacity = 1) => `rgba(0, 0, 0, ${opacity})`,
                }}
                bezier
                style={{
                  marginVertical: 8,
                }}
              />
            </Card.Content>
          </Card>
            </View>
          </LinearGradient>
        </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F5F5F5',
    },
    content: {
    padding: 15,
    },
    incomeCard: {
    elevation: 3,
    borderRadius: 10,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
    textAlign: 'center',
    color: '#4A4A4A',
  },
  incomeCard: {
    marginBottom: 20,
    elevation: 3,
    borderRadius: 10,
    backgroundColor: '#FFF',
  },
  incomeTitle: {
    fontSize: 18,
    color: '#4A4A4A',
  },
  incomeAmount: {
    fontSize: 28,
    fontWeight: 'bold',
    marginBottom: 10,
    color: '#0c51a1',
  },
  incomeDesc: {
    fontSize: 16,
    color: '#4A4A4A',
  },
  tenantsCard: {
    marginBottom: 20,
    elevation: 3,
    borderRadius: 10,
    backgroundColor: '#FFF',
    maxHeight: 150, // restricts the size to look sleeker
  },
  cardTitle: {
    fontSize: 22,
    fontWeight: 'bold',
    color: '#4A4A4A',
  },
  tenantName: {
    fontSize: 18,
    color: '#4A4A4A',
  },
  tenantRent: {
    fontSize: 16,
    color: '#333',
  },
  accordion: {
    paddingHorizontal: 10,
    },
  gradientContainer: {
    height: 380,
    width: '100%',
  },
});

export default LandlordDashboard;
