import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';
import { Input, Button, CheckBox } from 'react-native-elements';
import theme from '../../theme';

const SignupScreen = ({ navigation }) => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [isLandlord, setIsLandlord] = useState(false);

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Create Account</Text>

      <Input 
        placeholder='Username' 
        leftIcon={{ type: 'font-awesome', name: 'user', color: theme.colors.gray }}
        containerStyle={styles.inputContainer}
        inputStyle={styles.inputText}
        onChangeText={text => setUsername(text)} 
      />
      <Input 
        placeholder='Email' 
        leftIcon={{ type: 'font-awesome', name: 'envelope', color: theme.colors.gray }} 
        containerStyle={styles.inputContainer}
        inputStyle={styles.inputText}
        onChangeText={text => setEmail(text)} 
      />
      <Input 
        placeholder='Password' 
        leftIcon={{ type: 'font-awesome', name: 'lock', color: theme.colors.gray }} 
        containerStyle={styles.inputContainer}
        inputStyle={styles.inputText}
        secureTextEntry 
        onChangeText={text => setPassword(text)} 
      />
      <Input 
        placeholder='Confirm Password' 
        leftIcon={{ type: 'font-awesome', name: 'lock', color: theme.colors.gray }} 
        containerStyle={styles.inputContainer}
        inputStyle={styles.inputText}
        secureTextEntry 
        onChangeText={text => setConfirmPassword(text)} 
      />

      <View style={styles.roleSelector}>
        <CheckBox 
          title='Landlord'
          checkedIcon='dot-circle-o'
          uncheckedIcon='circle-o'
          checked={isLandlord}
          onPress={() => setIsLandlord(true)}
        />
        <CheckBox 
          title='Tenant'
          checkedIcon='dot-circle-o'
          uncheckedIcon='circle-o'
          checked={!isLandlord}
          onPress={() => setIsLandlord(false)}
        />
      </View>

      <Button 
        title="Sign Up" 
        buttonStyle={styles.signupButton}
        onPress={() => {/* Firebase signup code here */}} 
      />

      <Text style={styles.loginText}>Already have an account?</Text>
      <Button 
        title="Login" 
        type="clear" 
        titleStyle={styles.loginButtonText}
        onPress={() => navigation.navigate('Login')} 
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    paddingHorizontal: theme.spacing.medium,
    backgroundColor: theme.colors.background,
  },
  title: {
    fontSize: 28,
    fontWeight: 'bold',
    textAlign: 'center',
    marginBottom: theme.spacing.large,
  },
  inputContainer: {
    marginBottom: theme.spacing.medium,
  },
  inputText: {
    fontSize: 16,
  },
  roleSelector: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: theme.spacing.medium,
  },
  signupButton: {
    backgroundColor: theme.colors.primary.main,
    paddingVertical: theme.spacing.medium,
    marginBottom: theme.spacing.medium,
  },
  loginText: {
    textAlign: 'center',
    marginBottom: theme.spacing.small,
  },
  loginButtonText: {
    color: theme.colors.primary.main,
    fontSize: 16,
  }
});

export default SignupScreen;
