import React, { useState } from 'react';
import { 
    TextField, 
    Button, 
    Typography, 
    Container, 
    Box,
    InputAdornment,
    IconButton
} from '@mui/material';
import { User as UserIcon, Lock as LockIcon } from '@mui/icons-material';

const theme = {
    spacing: 8,  // Sample spacing value, adjust as necessary
};

const LoginScreen = ({ navigation }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    return (
        <Container maxWidth="xs">
            <Box 
                display="flex" 
                flexDirection="column" 
                justifyContent="center" 
                height="100vh"
            >
                <Typography variant="h4" align="center" gutterBottom>
                    Welcome Back!
                </Typography>

                <TextField 
                    fullWidth
                    variant="outlined"
                    margin="normal"
                    placeholder="Username"
                    InputProps={{
                        startAdornment: (
                            <InputAdornment position="start">
                                <UserIcon color="action" />
                            </InputAdornment>
                        ),
                    }}
                    onChange={e => setUsername(e.target.value)}
                />
                <TextField 
                    fullWidth
                    variant="outlined"
                    margin="normal"
                    placeholder="Password"
                    type="password"
                    InputProps={{
                        startAdornment: (
                            <InputAdornment position="start">
                                <LockIcon color="action" />
                            </InputAdornment>
                        ),
                    }}
                    onChange={e => setPassword(e.target.value)}
                />

                <Button 
                    variant="contained" 
                    color="primary" 
                    fullWidth 
                    style={{ margin: theme.spacing }}
                    onClick={() => {/* Firebase login code here */}}
                >
                    Login
                </Button>

                {/* Commented out Biometrics functionality for brevity, but you can integrate similar functionality using libraries available for the web. */}

                <Typography align="center" style={{ marginTop: theme.spacing }}>
                    Don't have a Cazza account?
                </Typography>
                <Button 
                    variant="text" 
                    color="primary" 
                    fullWidth
                    onClick={() => navigation.navigate('Signup')}
                >
                    Sign Up
                </Button>
            </Box>
        </Container>
    );
};

export default LoginScreen;
