const theme = {
  // Colors
  colors: {
    primary: {
      light: '#6ec6ff',
      main: '#2196f3',
      dark: '#0069c0',
    },
    secondary: {
      light: '#ff9a8d',
      main: '#ff6b6b',
      dark: '#c73840',
    },
    tertiary: {
      light: '#a7ff83',
      main: '#76ff03',
      dark: '#32cb00',
    },
    white: '#FFFFFF',
    black: '#000000',
    grey: {
      light: '#F7F7F7',
      main: '#9E9E9E',
      dark: '#424242',
    },
    error: '#D32F2F',
    warning: '#FBC02D',
    info: '#1976D2',
    success: '#388E3C',
  },

  // Typography
  typography: {
    header: {
      fontSize: 24,
      fontWeight: 'bold',
      color: '#212121',
    },
    subHeader: {
      fontSize: 20,
      fontWeight: '500',
      color: '#212121',
    },
    body: {
      fontSize: 16,
      fontWeight: '400',
      color: '#757575',
    },
    caption: {
      fontSize: 12,
      fontWeight: '300',
      color: '#9E9E9E',
    },
  },

  // Spacing
  spacing: {
    xsmall: 4,
    small: 8,
    medium: 16,
    large: 32,
    xlarge: 64,
  },

  // Border Radius
  borderRadius: {
    small: 4,
    medium: 8,
    large: 16,
  },
};

export default theme;
