import React, { useState } from 'react';
import { 
    TextField, 
    Button, 
    Typography, 
    Container, 
    Box, 
    InputAdornment,
    FormControlLabel,
    RadioGroup,
    Radio
} from '@mui/material';
import { User as UserIcon, Email as EmailIcon, Lock as LockIcon } from '@mui/icons-material';

const theme = {
    spacing: 8,  // Sample spacing value, adjust as necessary
};

const SignupScreen = ({ navigation }) => {
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [isLandlord, setIsLandlord] = useState(false);

    return (
        <Container maxWidth="xs">
            <Box 
                display="flex" 
                flexDirection="column" 
                justifyContent="center" 
                height="100vh"
            >
                <Typography variant="h4" align="center" gutterBottom>
                    Create Account
                </Typography>

                {/* ... Input fields similar to previous conversion ... */}
                <TextField 
                    fullWidth
                    variant="outlined"
                    margin="normal"
                    placeholder="Username"
                    InputProps={{
                        startAdornment: (
                            <InputAdornment position="start">
                                {/* <UserIcon color="action" /> */}
                            </InputAdornment>
                        ),
                    }}
                    onChange={e => setUsername(e.target.value)}
                />
                {/* ... Other input fields ... */}

                <RadioGroup 
                    row
                    value={isLandlord ? 'landlord' : 'tenant'}
                    onChange={e => setIsLandlord(e.target.value === 'landlord')}
                >
                    <FormControlLabel 
                        value="landlord" 
                        control={<Radio color="primary" />} 
                        label="Landlord" 
                    />
                    <FormControlLabel 
                        value="tenant" 
                        control={<Radio color="primary" />} 
                        label="Tenant" 
                    />
                </RadioGroup>

                <Button 
                    variant="contained" 
                    color="primary" 
                    fullWidth 
                    style={{ margin: theme.spacing }}
                    onClick={() => {/* Firebase signup code here */}}
                >
                    Sign Up
                </Button>

                <Typography align="center" style={{ marginTop: theme.spacing }}>
                    Already have an account?
                </Typography>
                <Button 
                    variant="text" 
                    color="primary" 
                    fullWidth
                    onClick={() => navigation.navigate('Login')}
                >
                    Login
                </Button>
            </Box>
        </Container>
    );
};

export default SignupScreen;
